/* Company Details */
INSERT INTO company_details (id, title, description, image, about_title, about_description, about_image, address, phone, email, google_map, social_media)
VALUES  (1, '{"en": "Production of packages for your products", "ru": "Изготовление упаковок для вашей продукции", "uz": "Mahsulotlaringiz uchun qadoqlarni tayyorlash"}',
         '{"en": "<p>With advanced technology of gravure printing on polyethylene and polypropylene for packaging and packaging of consumer products</p>", "ru": "<p>C передовой технологией глубокой печати на полиэтилене и полипропилене для упаковки и фасовки продукции потребления</p>", "uz": "<p>Iste''mol mahsulotlarini qadoqlash va qadoqlash uchun polietilen va polipropilendagi gravür bosib chiqarishning ilg''or texnologiyasi bilan</p>"}',
         'hero-section/recGd2t3rrGcvGikmgDIRr3YXu4HgLUpvgjJ41Sf.png',
         '{"en": "Offers high-quality printed-laminated products in the form of packaging or in rolls", "ru": "Предлагает высококачественную печатную-ламинированную продукцию в виде фасовочной упаковки или в рулонах", "uz": "Paket shaklida yoki rulonli yuqori sifatli bosilgan laminatlangan mahsulotlarni taklif etadi"}',
         '{"en": "<p>Golden Pack was founded in 2019 in the city of Tashkent and has a production printing and packaging lines consisting of high-performance and technological equipment.</p><p>The maximum production capacity is more than 100 tons of printed products per month. The production line is capable of producing printed products with high contrast and in a range of eight colors.</p><p>The partners-suppliers of raw materials for the Golden Pack company are well-known foreign Turkish and Chinese manufacturers.</p><p>In a short period of time, such companies as LLC \\"Best Tea\\", LLC \\"Yunon Pharm\\", LLC \\"Sof Gigienik\\", LLC \\"Tanamos Productions\\" and so on have trusted us and become our regular customers.</p>", "ru": "<p>Golden Pack основана в 2019 году в городе Ташкенте и имеет производственную печатную и упаковочную линии, состоящие из высокопроизводительного и технологического оборудования.</p><p>Максимальная производственная мощность более 100 тонн печатной продукции ежемесячно. Производственная линия способна производить печатную продукцию с высоким контрастом и в гамме, из восьми цветов.</p><p>Партнерами-поставщиками сырья для компании Golden Pack являются известные зарубежные турецкие и китайские производители.</p><p>За короткий промежуток времени нам доверились и стали нашими постоянными клиентами такие компании как ООО «Best Tea», ООО «Yunon Pharm», ООО «Sof Gigienik», ООО «Tanamos Productions» и тд.</p>", "uz": "<p>Golden Pack 2019 yilda Toshkent shahrida tashkil etilgan bo''lib, yuqori samarali va texnologik uskunalardan iborat bosma va qadoqlash ishlab chiqarish liniyasiga ega.</p><p>Maksimal ishlab chiqarish quvvati - oyiga 100 tonnadan ortiq bosma mahsulot. Ishlab chiqarish liniyasi yuqori kontrastli va sakkizta rangdagi bosma mahsulotlarni ishlab chiqarishga qodir.</p><p>Golden Pack uchun xomashyo etkazib beruvchilarning sheriklari taniqli xorijiy turk va xitoylik ishlab chiqaruvchilardir.</p><p>Qisqa vaqt ichida \\"Best Choy\\" MChJ, \\"Yunon Pharm\\" MChJ, \\"Sof Gigienik\\" MChJ, \\"Tanamos Productions\\" MChJ va boshqalar kabi kompaniyalar bizga ishonib, doimiy mijozlarimizga aylanishdi.</p>"}',
         'about/szMBTaJzbGhgL2sSfYEuoS69TvH17Zw3PtrHpa0U.jpg',
         '{"en": "Tashkent region, Tashkent district, «Choshtepa» KFY, Yunusabad-16 landmark: Turkistan restourant.", "ru": "Ташкенская обл., Ташкентский район, «Чоштепа» КФЙ, ориентир: Ташкент, Юнусабад 16 кв-л. ресторан «Туркистан».", "uz": "Toshkent viloyati, Toshkent tumani,«Choshtepa» QFY, Yunusobod 16 kv.l. mo''ljal: Turkiston restorani."}',
         '["+998 (71) 293-33-33", "+998 (97) 379-79-99"]', '["info@goldenpack.uz"]',
         'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2993.59206105294!2d69.29919361527628!3d41.382946954233645!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x38aef325df02670f%3A0xb755e6c0c3e469ab!2sYunusabad-16%2C%20Tashkent%20100180%2C%20Uzbekistan!5e0!3m2!1sen!2s!4v1625331158059!5m2!1sen!2s',
         '[{"url": "https://t.me/goldenpackchannel", "name": "telegram"}, {"url": null, "name": "facebook"}, {"url": null, "name": "instagram"}, {"url": null, "name": "linkedin"}]');

/* Process */
INSERT INTO processes (id, title, description, image, position)
VALUES  (1, '{"en": "Polyethylene printing", "ru": "Печать на полиэтилене", "uz": "Polietilen ustida chop etish"}', '{"en": "<p>Printing on polyethylene (LDPE, HDPE) and polypropylene (BOPP, CPP, PT).</p>", "ru": "<p>Печать на полиэтилене (LDPE, HDPE) и полипропилене (BOPP, CPP, PT).</p>", "uz": "<p>Polietilen (LDPE, HDPE) va polipropilen (BOPP, CPP, PT) chop etish.</p>"}', 'processes/L6DLOyBqHWJWqg1RNSy0rme1LA8DThdbHQWJUXhW.svg', 1),
        (2, '{"en": "Lamination of printed products", "ru": "Ламинация печатной продукции", "uz": "Laminatsiya bosma mahsulotlari"}', '{"en": "<p>Cold and hot lamination. One-sided and two-sided coating of products.</p>", "ru": "<p>Холодное и горячее ламинирование. Одностороннее и двухстороннее покрытие продукции.</p>", "uz": "<p>Sovuq va issiq laminatsiya. Bir tomonlama va ikki tomonlama qoplama mahsulotlari.</p>"}', 'processes/U443aIrlnu7WB5NA4ZMrrPbteKgiNRmYQYV6ztwo.svg', 2),
        (3, '{"en": "Cutting the packaging", "ru": "Обрезка упаковки", "uz": "Paketni kesib oling"}', '{"en": "<p>3- and 4-sided soldering according to packaging design.</p>", "ru": "<p>3х и 4х стороннее спаивание по дизайну упаковки.</p>", "uz": "<p>3x va 4x uchinchi tomon lehimleme qadoqlash dizayn.</p>"}', 'processes/vd54iaiWHmfgWycDK060yZS0Bqc6k7Di4SVDZTZU.svg', 3),
        (4, '{"en": "Installing elements", "ru": "Установка элементов", "uz": "Elementlarni o''rnatish"}', '{"en": "<p>Grippers and locks of the zip-lock system</p>", "ru": "<p>Грипперы и замки системы zip-lock</p>", "uz": "<p>Grippers and zip-locksZip - lock tizimining grippi va qulflari</p>"}', 'processes/b8L2TWGomkO9wXAN83DFot2LcXwxbfuSdDlyDMNe.svg', 4);

/* Equipment */
INSERT INTO equipment (id, title, description, image, position)
VALUES  (1, '{"en": "ZONBON ZBAY 1250", "ru": "ZONBON ZBAY 1250", "uz": "ZONBON ZBAY 1250"}', '{"en": "<p>The Zonbon ZBAY 1250 press is equipped with the latest servo technology (always using the latest Bosch Rexroth technology and equipment), software and an intuitive operator interface. This machine is equipped with all the features to ensure the highest possible productivity.</p>", "ru": "<p>Печатная машина Zonbon ZBAY 1250 оснащена новейшей технологией сервопривода (всегда с использованием новейших технологий и оборудования Bosch Rexroth), программным обеспечением и интуитивно-понятным интерфейсом оператора. Эта машина оснащена всеми функциями для обеспечения максимально возможной производительности.</p>", "uz": "<p>Zenon Z BOY 1250 bosib chiqarish mashinasi eng so''nggi servo haydovchi texnologiyasi (har doim eng so''nggi Bosch Rexroth texnologiyasi va uskunalari yordamida), dasturiy ta''minot va intuitiv operator interfeysi bilan jihozlangan. Ushbu mashina eng yuqori ish faoliyatini ta''minlash uchun barcha funktsiyalar bilan jihozlangan.</p>"}', 'equipment/66JX1UUZcsBhKDtahwGNWumHTiQkl0psII2Hnr1M.png', 1),
        (2, '{"en": "ZONBON ZBAY 9989", "ru": "ZONBON ZBAY 9989", "uz": "ZONBON ZBAY 9989"}', '{"en": "<p>The Zenon Z BOY 9989 printing machine is equipped with the latest servo technology (always using the latest Bosch Rexroth technologies and equipment), software and an intuitive operator interface. This machine is equipped with all the features to ensure the highest possible performance.</p>", "ru": "<p>Печатная машина Zonbon ZBAY 9989 оснащена новейшей технологией сервопривода (всегда с использованием новейших технологий и оборудования Bosch Rexroth), программным обеспечением и интуитивно-понятным интерфейсом оператора. Эта машина оснащена всеми функциями для обеспечения максимально возможной производительности.</p>", "uz": "<p>Zonbon ZBAY 9989 pressi eng so''nggi servo texnologiyasi (har doim eng yangi Bosch Rexroth texnologiyasi va uskunasidan foydalangan holda), dasturiy ta''minot va intuitiv operator interfeysi bilan jihozlangan. Ushbu mashina mumkin bo''lgan eng yuqori mahsuldorlikni ta''minlash uchun barcha funktsiyalar bilan jihozlangan.</p>"}', 'equipment/wwZEBn4moZN9STZOsgNJaS2okgHUsMTDJTHUVQYF.png', 2);

/* Portfolio */
INSERT INTO portfolios (id, title, description, image, position)
VALUES  (1, '{"en": "Snack packaging", "ru": "Упаковка для закусок", "uz": "Tamadi mahsulotlari uchun qadoqlash"}', '{"en": "<p>We would like to present to you our new work, in which we tested several hypotheses, tested packaging options for the target audience and chose, as it seems to us, the most suitable one.</p>", "ru": "<p>Хотим вам презентовать нашу новую работу, в которой мы проверяли несколько гипотез, тестировали варианты упаковки на целевой аудитории и выбрали, как нам кажется, самый подходящий.</p>", "uz": "<p>Sizlarga yangi ishimizni taqdim etmoqchimiz, unda bir nechta gipotezalarni sinovdan o''tkazdik, maqsadli auditoriyada qadoqlash variantlarini sinab ko''rdik va bizning fikrimizcha eng mosini tanladik.</p>"}', 'portfolios/gBqzNJPBbfHSkQ1WzQMmrYeBygWxz6AslDv78fLw.png', 1),
