INSERT INTO testimonials (id, name, title, description, image, position)
VALUES  (1, 'Billie Eilish', '{"en": "General Director", "ru": "Гендиректор", "uz": "Bosh ijrochi direktor"}', '{"en": "<p>We express our gratitude to the Golden Pack company for fruitful cooperation, for good working contacts between the companies, we wish you industrial success, economic and financial well-being</p>", "ru": "<p>Выражаем благодарность компании Golden Pack за плодотворное сотрудничество, за хорошие рабочие контакты между фирмами, желаем производственных успехов, экономического и финансового благополучия</p>", "uz": "<p>Golden Pack kompaniyasiga samarali hamkorlik uchun, kompaniyalar o''rtasidagi yaxshi ish aloqalari uchun o''z minnatdorchiligimizni bildiramiz, sizga sanoat yutuqlari, iqtisodiy va moliyaviy farovonlik tilaymiz</p>"}', 'testimonials/bEzmsLPWzyzl3W7jRFBQwc92V19lh8R1MjKoReB1.jpg', 1),
        (2, 'Scarlet Johansson', '{"en": "General Director", "ru": "Гендиректор", "uz": "Bosh direktor"}', '{"en": "<p>We express our gratitude to the Golden Pack company for fruitful cooperation, for good working contacts between the companies, we wish you industrial success, economic and financial well-being</p>", "ru": "<p>Выражаем благодарность компании Golden Pack за плодотворное сотрудничество, за хорошие рабочие контакты между фирмами, желаем производственных успехов, экономического и финансового благополучия</p>", "uz": "<p>Golden Pack kompaniyasiga samarali hamkorlik uchun, kompaniyalar o''rtasidagi yaxshi ish aloqalari uchun o''z minnatdorchiligimizni bildiramiz, sizga sanoat yutuqlari, iqtisodiy va moliyaviy farovonlik tilaymiz</p>"}', 'testimonials/wmcnDYwwufnK2tBmzLiJLx9Ek13NpwO7VqPNcmOe.jpg', 2);

/* Advantages */
INSERT INTO advantages (id, title, description, image, position)
VALUES  (1, '{"en": "Free shipping", "ru": "Бесплатная доставка", "uz": "Yetkazib berish bepul"}', '{"en": "<p>Free delivery of products within the city of Tashkent and Tash. regions</p>", "ru": "<p>Бесплатная доставка продукции в пределах г. Ташкент и Таш. области</p>", "uz": "<p>Mahsulotlarni Toshkent va Tosh shaharlarida bepul etkazib berish maydonlar</p>"}', 'advantages/bhhviEPTdf0mqA3Xw70y23u13yWiVNeAsscsDnep.svg', 1),
        (2, '{"en": "Free consultation", "ru": "Бесплатная консультация", "uz": "Bepul maslahat"}', '{"en": "<p>Free consultation of experienced Golden Pack specialists</p>", "ru": "<p>Бесплатная консультация опытных специалистов Golden Pack</p>", "uz": "<p>Golden Pack tajribali mutaxassislarining bepul maslahati</p>"}', 'advantages/TalD0yX2IdRx4B4LxpegfyhxPIeJNmBT3uFpJVWq.svg', 2),
        (3, '{"en": "Free production", "ru": "Бесплатное изготовление", "uz": "Bepul ishlab chiqarish"}', '{"en": "<p>Free production of the printed shaft as part of a personal bonus</p>", "ru": "<p>Бесплатное изготовление печатного вала в рамках персонального бонуса</p>", "uz": "<p>Shaxsiy bonus sifatida bosma rolni bepul ishlab chiqarish</p>"}', 'advantages/L4ziitY7w4UCOl01RxorRJEqh9UyhoaoNK7HWyS3.svg', 3),
        (4, '{"en": "Refund of funds", "ru": "Возврат средств", "uz": "Pulni qaytarish"}', '{"en": "<p>Guaranteed 100% refund if all or part of the products are defective</p>", "ru": "<p>Гарантированный 100% возврат средств при браке всей или части продукции</p>", "uz": "<p>Mahsulotning barchasi yoki bir qismi nuqsonli bo''lsa, 100% qaytarib berishga kafolat beradi</p>"}', 'advantages/GIdCe9VPg7GGiD40OVWyJpv8kuBkujtTD8cS64iz.svg', 4),
        (5, '{"en": "The best prices on the market", "ru": "Лучшие цены на рынке", "uz": "Bozorda eng yaxshi narxlar"}', '{"en": "<p>We do everything on time, efficiently and at low prices</p>", "ru": "<p>Делаем все вовремя, качественно и по невысоким ценам</p>", "uz": "<p>Biz hamma narsani o''z vaqtida, sifatli va arzon narxlarda qilamiz</p>"}', 'advantages/kgRmeD9owm8UnCd32Nv302RKjXglwgBwvvCcDugI.svg', 5),
        (6, '{"en": "Work experience", "ru": "Опыт работы", "uz": "Ish tajribasi"}', '{"en": "<p>Many years of experience, we are always up to date with the latest technical innovations</p>", "ru": "<p>Многолетний опыт работы, мы всегда в курсе последних технических новинок</p>", "uz": "<p>Ko''p yillik tajribamiz, biz har doim eng so''nggi texnik yangiliklardan xabardor bo''lamiz</p>"}', 'advantages/DaIf2UkORK1oWG5O25sOFFq6StCxAOI3Sd44f5dU.svg', 6);

/* Cooperation */
INSERT INTO cooperations (id, list, image)
VALUES  (1, '[{"en": {"title": "Lending and consulting", "description": "<p>Our staff will help you choose the most favorable loan terms, tell you about all the subtleties and nuances, and, if necessary, help you collect all the necessary documents. The loan can be quickly issued directly at our office.</p>"}, "ru": {"title": "Кредитование и консультация", "description": "<p>Наши сотрудники помогут Вам подобрать наиболее выгодные условия кредитования, расскажут обо всех тонкостях и нюансах, а также, при необходимости, помогут собрать все необходимые документы. Кредит можно оперативно оформить прямо у нас в офисе.</p>"}, "uz": {"title": "Kredit berish va konsalting", "description": "<p>Bizning xodimlarimiz sizga eng maqbul kredit shartlarini tanlashda, barcha nozikliklar va nuanslar haqida aytib berishda yordam berishadi va agar kerak bo''lsa, barcha kerakli hujjatlarni to''plashda yordam berishadi. Kreditni bizning ofisimizda tezda rasmiylashtirish mumkin.</p>"}}, {"en": {"title": "Warranty service", "description": "<p>For any of our products, both free warranty service and paid post-warranty support are provided.</p>"}, "ru": {"title": "Гарантийное обслуживание", "description": "<p>На любой наш товар предоставляется как бесплатное гарантийное обслуживание, так и платная послегарантийная поддержка.</p>"}, "uz": {"title": "Kafolat xizmati", "description": "<p>Bizning har qanday mahsulotimiz uchun bepul kafolat xizmati ham, kafolatdan keyingi pullik yordam ham taqdim etiladi.</p>"}}, {"en": {"title": "Redemption and exchange", "description": "<p>The products purchased from us no longer satisfy your needs? Did you have a desire to buy a new one? It is enough just to contact our professional appraisers, tell them what your product is, what condition it is in, what price you want to set or exchange for, etc., and our employees will save you from unnecessary hassle.</p>"}, "ru": {"title": "Выкуп и обмен", "description": "<p>Купленная у нас продукция более не удовлетворяет Вашим запросам? Появилось желание приобрести новую? Достаточно просто связаться с нашими профессионалами-оценщиками, сообщить им, что у Вас за товар, в каком он состоянии, какую цену Вы хотите назначить или на что обменять и пр., и наши сотрудники избавят Вас от ненужных хлопот.</p>"}, "uz": {"title": "Qutqarish va almashtirish", "description": "<p>Bizdan sotib olingan mahsulotlar endi sizning ehtiyojlaringizni qondirmaydimi? Yangisini sotib olish istagi bormi? Bizning professional baholovchilarimiz bilan bog''lanish, tovarlarga nima borligini, ular qanday holatda, qanday narx belgilashni xohlayotganingizni yoki nimani almashtirishni va hokazolarni aytib berish kifoya, va bizning xodimlarimiz sizni ortiqcha muammolardan xalos qiladi.</p>"}}, {"en": {"title": "Ordering components, accessories and much more", "description": "<p>In our stores, you can always place an order for the product you are interested in. Even if the desired product is not available or even in the catalog, you can always leave a request with our staff and get a response to your request in the near future.</p>"}, "ru": {"title": "Заказ комплектующих, аксессуаров и много другого", "description": "<p>В наших магазинах Вы всегда можете оформить заказ на интересующий Вас товар. Даже если искомого товара нет в наличии или вообще в каталоге, Вы всегда можете оставить заявку у наших сотрудников и в ближайшее время получить отклик на свой запрос.</p>"}, "uz": {"title": "Komponentlarga, aksessuarlarga va boshqa ko''p narsalarga buyurtma berish", "description": "<p>Bizning do''konlarda har doim o''zingizni qiziqtirgan mahsulotga buyurtma berishingiz mumkin. Agar siz izlayotgan mahsulot zaxirada bo''lmasa yoki hatto katalogda bo''lsa ham, siz har doim bizning xodimlarimizga so''rov qoldirishingiz va yaqin orada sizning so''rovingizga javob olishingiz mumkin.</p>"}}]', 'cooperations/E1z7uF1i7uLX1D2h5v3HNaKETtN9UWa2EauNA0bv.png');
